import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [customers, setCustomers] = useState([]);
  const [deliveries, setDeliveries] = useState([]);
  const [bills, setBills] = useState([]);
  const [activeTab, setActiveTab] = useState('customers');
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    address: '',
    daily_milk_quantity: '',
    milk_rate_per_liter: ''
  });
  const [editingId, setEditingId] = useState(null);

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedCustomers = localStorage.getItem('milk_customers');
    const savedDeliveries = localStorage.getItem('milk_deliveries');
    const savedBills = localStorage.getItem('milk_bills');

    if (savedCustomers) setCustomers(JSON.parse(savedCustomers));
    if (savedDeliveries) setDeliveries(JSON.parse(savedDeliveries));
    if (savedBills) setBills(JSON.parse(savedBills));
  }, []);

  // Save data to localStorage whenever state changes
  useEffect(() => {
    localStorage.setItem('milk_customers', JSON.stringify(customers));
  }, [customers]);

  useEffect(() => {
    localStorage.setItem('milk_deliveries', JSON.stringify(deliveries));
  }, [deliveries]);

  useEffect(() => {
    localStorage.setItem('milk_bills', JSON.stringify(bills));
  }, [bills]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (editingId) {
      // Update existing customer
      setCustomers(customers.map(c => 
        c.id === editingId ? { ...formData, id: editingId } : c
      ));
      setEditingId(null);
    } else {
      // Add new customer
      const newCustomer = {
        ...formData,
        id: Date.now(), // Simple ID generation
        daily_milk_quantity: parseFloat(formData.daily_milk_quantity) || 0,
        milk_rate_per_liter: parseFloat(formData.milk_rate_per_liter) || 0,
        created_at: new Date().toISOString()
      };

      // Check for duplicate phone numbers
      const existingCustomer = customers.find(c => c.phone === formData.phone);
      if (existingCustomer) {
        alert('Phone number already exists!');
        return;
      }

      setCustomers([...customers, newCustomer]);
    }

    // Reset form
    setFormData({
      name: '',
      phone: '',
      address: '',
      daily_milk_quantity: '',
      milk_rate_per_liter: ''
    });
  };

  const handleEdit = (customer) => {
    setFormData({
      name: customer.name,
      phone: customer.phone,
      address: customer.address,
      daily_milk_quantity: customer.daily_milk_quantity,
      milk_rate_per_liter: customer.milk_rate_per_liter
    });
    setEditingId(customer.id);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this customer?')) {
      setCustomers(customers.filter(c => c.id !== id));
      // Also remove related deliveries and bills
      setDeliveries(deliveries.filter(d => d.customer_id !== id));
      setBills(bills.filter(b => b.customer_id !== id));
    }
  };

  const generateBill = (customerId) => {
    const customer = customers.find(c => c.id === customerId);
    if (!customer) return;

    const today = new Date();
    const newBill = {
      id: Date.now(),
      customer_id: customerId,
      customer_name: customer.name,
      bill_month: today.getMonth() + 1,
      bill_year: today.getFullYear(),
      total_quantity: customer.daily_milk_quantity * 30, // Assuming 30 days
      total_amount: customer.daily_milk_quantity * 30 * customer.milk_rate_per_liter,
      bill_generated_at: today.toISOString(),
      sent_to_customer: false
    };

    setBills([...bills, newBill]);
    alert(`Bill generated for ${customer.name} - Amount: ₹${newBill.total_amount.toFixed(2)}`);
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Milk Delivery Management</h1>
      </header>

      <nav className="tabs">
        <button 
          className={activeTab === 'customers' ? 'active' : ''}
          onClick={() => setActiveTab('customers')}
        >
          Customers
        </button>
        <button 
          className={activeTab === 'deliveries' ? 'active' : ''}
          onClick={() => setActiveTab('deliveries')}
        >
          Deliveries
        </button>
        <button 
          className={activeTab === 'bills' ? 'active' : ''}
          onClick={() => setActiveTab('bills')}
        >
          Bills
        </button>
      </nav>

      <main className="main-content">
        {activeTab === 'customers' && (
          <div className="customers-section">
            <form onSubmit={handleSubmit} className="customer-form">
              <h2>{editingId ? 'Edit Customer' : 'Add New Customer'}</h2>

              <input
                type="text"
                name="name"
                placeholder="Customer Name"
                value={formData.name}
                onChange={handleChange}
                required
              />

              <input
                type="tel"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
                required
              />

              <textarea
                name="address"
                placeholder="Address"
                value={formData.address}
                onChange={handleChange}
                required
              ></textarea>

              <input
                type="number"
                name="daily_milk_quantity"
                placeholder="Daily Milk Quantity (L)"
                value={formData.daily_milk_quantity}
                onChange={handleChange}
                step="0.01"
                min="0"
              />

              <input
                type="number"
                name="milk_rate_per_liter"
                placeholder="Milk Rate per Liter (₹)"
                value={formData.milk_rate_per_liter}
                onChange={handleChange}
                step="0.01"
                min="0"
              />

              <button type="submit">{editingId ? 'Update' : 'Add'} Customer</button>

              {editingId && (
                <button type="button" onClick={() => {
                  setEditingId(null);
                  setFormData({
                    name: '',
                    phone: '',
                    address: '',
                    daily_milk_quantity: '',
                    milk_rate_per_liter: ''
                  });
                }}>
                  Cancel
                </button>
              )}
            </form>

            <div className="customer-list">
              <h2>Customer List ({customers.length})</h2>

              {customers.length === 0 ? (
                <p>No customers added yet.</p>
              ) : (
                <table className="customer-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Phone</th>
                      <th>Address</th>
                      <th>Daily Qty (L)</th>
                      <th>Rate (₹/L)</th>
                      <th>Total Monthly (₹)</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {customers.map(customer => (
                      <tr key={customer.id}>
                        <td>{customer.name}</td>
                        <td>{customer.phone}</td>
                        <td>{customer.address}</td>
                        <td>{customer.daily_milk_quantity}</td>
                        <td>{customer.milk_rate_per_liter}</td>
                        <td>
                          {(customer.daily_milk_quantity * 30 * customer.milk_rate_per_liter).toFixed(2)}
                        </td>
                        <td>
                          <button onClick={() => handleEdit(customer)}>Edit</button>
                          <button onClick={() => handleDelete(customer.id)}>Delete</button>
                          <button onClick={() => generateBill(customer.id)}>Generate Bill</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </div>
        )}

        {activeTab === 'deliveries' && (
          <div className="deliveries-section">
            <h2>Delivery Management</h2>
            <p>Delivery tracking functionality coming soon...</p>
            {/* Delivery management interface would go here */}
          </div>
        )}

        {activeTab === 'bills' && (
          <div className="bills-section">
            <h2>Bill Management</h2>

            {bills.length === 0 ? (
              <p>No bills generated yet.</p>
            ) : (
              <table className="bills-table">
                <thead>
                  <tr>
                    <th>Customer</th>
                    <th>Month/Year</th>
                    <th>Quantity (L)</th>
                    <th>Amount (₹)</th>
                    <th>Date Generated</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {bills.map(bill => (
                    <tr key={bill.id}>
                      <td>{bill.customer_name}</td>
                      <td>{bill.bill_month}/{bill.bill_year}</td>
                      <td>{bill.total_quantity.toFixed(2)}</td>
                      <td>{bill.total_amount.toFixed(2)}</td>
                      <td>{new Date(bill.bill_generated_at).toLocaleDateString()}</td>
                      <td>{bill.sent_to_customer ? 'Sent' : 'Pending'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
