import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [customers, setCustomers] = useState([]);
  const [deliveries, setDeliveries] = useState([]);
  const [bills, setBills] = useState([]);
  const [payments, setPayments] = useState([]); // Track payments
  const [activeTab, setActiveTab] = useState('customers');
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    address: '',
    daily_milk_quantity: '',
    milk_rate_per_liter: '',
    shift: 'morning', // morning, evening, occasional
    status: 'active' // active, inactive
  });
  const [editingId, setEditingId] = useState(null);
  const [selectedShift, setSelectedShift] = useState('all'); // For delivery view
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]); // For delivery view

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedCustomers = localStorage.getItem('milk_customers');
    const savedDeliveries = localStorage.getItem('milk_deliveries');
    const savedBills = localStorage.getItem('milk_bills');
    const savedPayments = localStorage.getItem('milk_payments');

    if (savedCustomers) setCustomers(JSON.parse(savedCustomers));
    if (savedDeliveries) setDeliveries(JSON.parse(savedDeliveries));
    if (savedBills) setBills(JSON.parse(savedBills));
    if (savedPayments) setPayments(JSON.parse(savedPayments));
  }, []);

  // Save data to localStorage whenever state changes
  useEffect(() => {
    localStorage.setItem('milk_customers', JSON.stringify(customers));
  }, [customers]);

  useEffect(() => {
    localStorage.setItem('milk_deliveries', JSON.stringify(deliveries));
  }, [deliveries]);

  useEffect(() => {
    localStorage.setItem('milk_bills', JSON.stringify(bills));
  }, [bills]);

  useEffect(() => {
    localStorage.setItem('milk_payments', JSON.stringify(payments));
  }, [payments]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (editingId) {
      // Update existing customer
      setCustomers(customers.map(c =>
        c.id === editingId ? { ...formData, id: editingId } : c
      ));
      setEditingId(null);
    } else {
      // Add new customer
      const newCustomer = {
        ...formData,
        id: Date.now(), // Simple ID generation
        daily_milk_quantity: parseFloat(formData.daily_milk_quantity) || 0,
        milk_rate_per_liter: parseFloat(formData.milk_rate_per_liter) || 0,
        created_at: new Date().toISOString(),
        default_milk_quantity: parseFloat(formData.daily_milk_quantity) || 0 // Store default quantity
      };

      // Check for duplicate phone numbers
      const existingCustomer = customers.find(c => c.phone === formData.phone);
      if (existingCustomer) {
        alert('Phone number already exists!');
        return;
      }

      setCustomers([...customers, newCustomer]);
    }

    // Reset form
    setFormData({
      name: '',
      phone: '',
      address: '',
      daily_milk_quantity: '',
      milk_rate_per_liter: '',
      shift: 'morning',
      status: 'active'
    });
  };

  const handleEdit = (customer) => {
    setFormData({
      name: customer.name,
      phone: customer.phone,
      address: customer.address,
      daily_milk_quantity: customer.daily_milk_quantity,
      milk_rate_per_liter: customer.milk_rate_per_liter,
      shift: customer.shift || 'morning',
      status: customer.status || 'active'
    });
    setEditingId(customer.id);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this customer?')) {
      setCustomers(customers.filter(c => c.id !== id));
      // Also remove related deliveries and bills
      setDeliveries(deliveries.filter(d => d.customer_id !== id));
      setBills(bills.filter(b => b.customer_id !== id));
    }
  };

  // Function to generate bill with balance calculation
  const generateBill = (customerId) => {
    const customer = customers.find(c => c.id === customerId);
    if (!customer) return;

    // Calculate any outstanding balance from previous bills
    const previousUnpaidBills = bills.filter(b =>
      b.customer_id === customerId &&
      !b.paid &&
      !(b.bill_month === new Date().getMonth() + 1 && b.bill_year === new Date().getFullYear())
    );

    const outstandingBalance = previousUnpaidBills.reduce((sum, bill) => sum + (bill.balance || bill.total_amount), 0);

    const today = new Date();
    const newBill = {
      id: Date.now(),
      customer_id: customerId,
      customer_name: customer.name,
      bill_month: today.getMonth() + 1,
      bill_year: today.getFullYear(),
      total_quantity: customer.daily_milk_quantity * 30, // Assuming 30 days
      total_amount: parseFloat((customer.daily_milk_quantity * 30 * customer.milk_rate_per_liter).toFixed(2)),
      bill_generated_at: today.toISOString(),
      sent_to_customer: false,
      paid: false,
      payment_date: null,
      amount_paid: 0,
      balance: parseFloat((customer.daily_milk_quantity * 30 * customer.milk_rate_per_liter + outstandingBalance).toFixed(2)),
      outstanding_balance: outstandingBalance
    };

    setBills([...bills, newBill]);
    alert(`Bill generated for ${customer.name} - Amount: ₹${newBill.balance.toFixed(2)} (includes ₹${outstandingBalance.toFixed(2)} outstanding)`);
  };

  // Function to mark bill as paid
  const markBillAsPaid = (billId, amountPaid, changeGiven = 0) => {
    setBills(bills.map(bill => {
      if (bill.id === billId) {
        const newAmountPaid = (bill.amount_paid || 0) + parseFloat(amountPaid || 0);
        const newBalance = parseFloat((bill.balance - amountPaid).toFixed(2));
        const paidStatus = newBalance <= 0;

        // Create a payment record
        const newPayment = {
          id: Date.now(),
          bill_id: billId,
          customer_id: bill.customer_id,
          amount_paid: parseFloat(amountPaid),
          change_given: parseFloat(changeGiven),
          payment_date: new Date().toISOString(),
          payment_method: 'cash' // Could be expanded to include other methods
        };

        setPayments([...payments, newPayment]);

        return {
          ...bill,
          paid: paidStatus,
          amount_paid: newAmountPaid,
          balance: newBalance,
          payment_date: paidStatus ? new Date().toISOString() : bill.payment_date
        };
      }
      return bill;
    }));
  };

  // Function to handle partial payment
  const handlePartialPayment = (billId, amountPaid, changeGiven = 0) => {
    if (parseFloat(amountPaid) <= 0) {
      alert('Please enter a valid payment amount');
      return;
    }

    const bill = bills.find(b => b.id === billId);
    if (!bill) return;

    if (parseFloat(amountPaid) > bill.balance) {
      alert(`Payment amount exceeds remaining balance of ₹${bill.balance}`);
      return;
    }

    markBillAsPaid(billId, amountPaid, changeGiven);
  };

  // Function to get customers by shift
  const getCustomersByShift = (shift) => {
    if (shift === 'all') {
      return customers.filter(c => c.status === 'active');
    }
    return customers.filter(c => c.shift === shift && c.status === 'active');
  };

  // Function to handle delivery marking
  const markDelivery = (customer, date, deliveredQty) => {
    const existingDelivery = deliveries.find(d =>
      d.customer_id === customer.id && d.date === date
    );

    if (existingDelivery) {
      // Update existing delivery
      setDeliveries(deliveries.map(d =>
        d.id === existingDelivery.id
          ? { ...d, delivered_quantity: deliveredQty, delivered: true, leave: false }
          : d
      ));
    } else {
      // Create new delivery record
      const newDelivery = {
        id: Date.now(),
        customer_id: customer.id,
        customer_name: customer.name,
        date: date,
        scheduled_quantity: customer.default_milk_quantity,
        delivered_quantity: deliveredQty,
        delivered: true,
        leave: false,
        delivery_shift: customer.shift
      };
      setDeliveries([...deliveries, newDelivery]);
    }
  };

  // Function to mark a leave (absence)
  const markLeave = (customer, date) => {
    const existingDelivery = deliveries.find(d =>
      d.customer_id === customer.id && d.date === date
    );

    if (existingDelivery) {
      // Update existing delivery as leave
      setDeliveries(deliveries.map(d =>
        d.id === existingDelivery.id
          ? { ...d, delivered: false, leave: true }
          : d
      ));
    } else {
      // Create new delivery record marked as leave
      const newDelivery = {
        id: Date.now(),
        customer_id: customer.id,
        customer_name: customer.name,
        date: date,
        scheduled_quantity: customer.default_milk_quantity,
        delivered_quantity: 0,
        delivered: false,
        leave: true,
        delivery_shift: customer.shift
      };
      setDeliveries([...deliveries, newDelivery]);
    }
  };

  // Function to handle extra milk addition
  const addExtraMilk = (customer, date, extraQty) => {
    const existingDelivery = deliveries.find(d =>
      d.customer_id === customer.id && d.date === date
    );

    let newDeliveredQty = customer.default_milk_quantity + parseFloat(extraQty || 0);

    if (existingDelivery) {
      // Update existing delivery with extra milk
      setDeliveries(deliveries.map(d =>
        d.id === existingDelivery.id
          ? { ...d, delivered_quantity: newDeliveredQty, delivered: true, leave: false }
          : d
      ));
    } else {
      // Create new delivery record with extra milk
      const newDelivery = {
        id: Date.now(),
        customer_id: customer.id,
        customer_name: customer.name,
        date: date,
        scheduled_quantity: customer.default_milk_quantity,
        delivered_quantity: newDeliveredQty,
        delivered: true,
        leave: false,
        delivery_shift: customer.shift,
        extra_milk: parseFloat(extraQty || 0)
      };
      setDeliveries([...deliveries, newDelivery]);
    }
  };

  // Function to override default quantity for a specific day
  const overrideDefaultQuantity = (customer, date, newQty) => {
    const existingDelivery = deliveries.find(d =>
      d.customer_id === customer.id && d.date === date
    );

    if (existingDelivery) {
      // Update existing delivery with overridden quantity
      setDeliveries(deliveries.map(d =>
        d.id === existingDelivery.id
          ? { ...d, delivered_quantity: parseFloat(newQty), delivered: true, leave: false }
          : d
      ));
    } else {
      // Create new delivery record with overridden quantity
      const newDelivery = {
        id: Date.now(),
        customer_id: customer.id,
        customer_name: customer.name,
        date: date,
        scheduled_quantity: customer.default_milk_quantity,
        delivered_quantity: parseFloat(newQty),
        delivered: true,
        leave: false,
        delivery_shift: customer.shift,
        quantity_overridden: true
      };
      setDeliveries([...deliveries, newDelivery]);
    }
  };

  // Get deliveries for selected date and shift
  const getDeliveriesForDate = () => {
    let filteredDeliveries = deliveries.filter(d => d.date === selectedDate);

    if (selectedShift !== 'all') {
      filteredDeliveries = filteredDeliveries.filter(d => d.delivery_shift === selectedShift);
    }

    return filteredDeliveries;
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Milk Delivery Management System</h1>
      </header>

      <nav className="tabs">
        <button
          className={activeTab === 'customers' ? 'active' : ''}
          onClick={() => setActiveTab('customers')}
        >
          Customers
        </button>
        <button
          className={activeTab === 'deliveries' ? 'active' : ''}
          onClick={() => setActiveTab('deliveries')}
        >
          Deliveries
        </button>
        <button
          className={activeTab === 'bills' ? 'active' : ''}
          onClick={() => setActiveTab('bills')}
        >
          Bills
        </button>
      </nav>

      <main className="main-content">
        {activeTab === 'customers' && (
          <div className="customers-section">
            <form onSubmit={handleSubmit} className="customer-form">
              <h2>{editingId ? 'Edit Customer' : 'Add New Customer'}</h2>

              <input
                type="text"
                name="name"
                placeholder="Customer Name"
                value={formData.name}
                onChange={handleChange}
                required
              />

              <input
                type="tel"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
                required
              />

              <textarea
                name="address"
                placeholder="Address"
                value={formData.address}
                onChange={handleChange}
                required
              ></textarea>

              <input
                type="number"
                name="daily_milk_quantity"
                placeholder="Daily Milk Quantity (L)"
                value={formData.daily_milk_quantity}
                onChange={handleChange}
                step="0.01"
                min="0"
              />

              <input
                type="number"
                name="milk_rate_per_liter"
                placeholder="Milk Rate per Liter (₹)"
                value={formData.milk_rate_per_liter}
                onChange={handleChange}
                step="0.01"
                min="0"
              />

              <select
                name="shift"
                value={formData.shift}
                onChange={handleChange}
              >
                <option value="morning">Morning</option>
                <option value="evening">Evening</option>
                <option value="occasional">Occasional</option>
              </select>

              <select
                name="status"
                value={formData.status}
                onChange={handleChange}
              >
                <option value="active">Active</option>
                <option value="inactive">Inactive</option>
              </select>

              <button type="submit">{editingId ? 'Update' : 'Add'} Customer</button>

              {editingId && (
                <button type="button" onClick={() => {
                  setEditingId(null);
                  setFormData({
                    name: '',
                    phone: '',
                    address: '',
                    daily_milk_quantity: '',
                    milk_rate_per_liter: '',
                    shift: 'morning',
                    status: 'active'
                  });
                }}>
                  Cancel
                </button>
              )}
            </form>

            <div className="customer-list">
              <h2>Customer List ({customers.length})</h2>

              {customers.length === 0 ? (
                <p>No customers added yet.</p>
              ) : (
                <table className="customer-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Phone</th>
                      <th>Address</th>
                      <th>Shift</th>
                      <th>Daily Qty (L)</th>
                      <th>Rate (₹/L)</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {customers.map(customer => (
                      <tr key={customer.id}>
                        <td>{customer.name}</td>
                        <td>{customer.phone}</td>
                        <td>{customer.address}</td>
                        <td>
                          <span className={`shift-badge ${customer.shift}`}>
                            {customer.shift.charAt(0).toUpperCase() + customer.shift.slice(1)}
                          </span>
                        </td>
                        <td>{customer.daily_milk_quantity}</td>
                        <td>{customer.milk_rate_per_liter}</td>
                        <td>
                          <span className={`status-badge ${customer.status}`}>
                            {customer.status.charAt(0).toUpperCase() + customer.status.slice(1)}
                          </span>
                        </td>
                        <td>
                          <button onClick={() => handleEdit(customer)}>Edit</button>
                          <button onClick={() => handleDelete(customer.id)}>Delete</button>
                          <button onClick={() => generateBill(customer.id)}>Generate Bill</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </div>
        )}

        {activeTab === 'deliveries' && (
          <div className="deliveries-section">
            <h2>Delivery Management</h2>

            <div className="delivery-controls">
              <label>
                Date:
                <input
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                />
              </label>

              <label>
                Shift:
                <select
                  value={selectedShift}
                  onChange={(e) => setSelectedShift(e.target.value)}
                >
                  <option value="all">All Shifts</option>
                  <option value="morning">Morning</option>
                  <option value="evening">Evening</option>
                  <option value="occasional">Occasional</option>
                </select>
              </label>
            </div>

            <div className="delivery-customers">
              <h3>Customers for {selectedDate} - {selectedShift === 'all' ? 'All Shifts' : selectedShift.charAt(0).toUpperCase() + selectedShift.slice(1)}</h3>

              {getCustomersByShift(selectedShift).length === 0 ? (
                <p>No active customers in this shift.</p>
              ) : (
                <div className="delivery-grid">
                  {getCustomersByShift(selectedShift).map(customer => {
                    const deliveryRecord = deliveries.find(d =>
                      d.customer_id === customer.id && d.date === selectedDate
                    );

                    return (
                      <div key={customer.id} className="delivery-card">
                        <h4>{customer.name}</h4>
                        <p>Phone: {customer.phone}</p>
                        <p>Default: {customer.default_milk_quantity}L</p>

                        <div className="delivery-actions">
                          <button
                            onClick={() => markDelivery(customer, selectedDate, customer.default_milk_quantity)}
                            className="btn-delivered"
                          >
                            Delivered (Default)
                          </button>

                          <button
                            onClick={() => markLeave(customer, selectedDate)}
                            className="btn-leave"
                          >
                            Mark Leave
                          </button>

                          <div className="extra-milk-control">
                            <input
                              type="number"
                              placeholder="Extra milk (L)"
                              min="0"
                              step="0.1"
                              onKeyDown={(e) => {
                                if (e.key === 'Enter') {
                                  const extraQty = e.target.value;
                                  if (extraQty) {
                                    addExtraMilk(customer, selectedDate, extraQty);
                                  }
                                }
                              }}
                            />
                            <button
                              onClick={(e) => {
                                const input = e.target.previousElementSibling;
                                if (input.value) {
                                  addExtraMilk(customer, selectedDate, input.value);
                                  input.value = '';
                                }
                              }}
                            >
                              Add Extra
                            </button>
                          </div>

                          <div className="override-control">
                            <input
                              type="number"
                              placeholder="Override qty (L)"
                              min="0"
                              step="0.1"
                              onKeyDown={(e) => {
                                if (e.key === 'Enter') {
                                  const newQty = e.target.value;
                                  if (newQty) {
                                    overrideDefaultQuantity(customer, selectedDate, newQty);
                                  }
                                }
                              }}
                            />
                            <button
                              onClick={(e) => {
                                const input = e.target.previousElementSibling;
                                if (input.value) {
                                  overrideDefaultQuantity(customer, selectedDate, input.value);
                                  input.value = '';
                                }
                              }}
                            >
                              Override
                            </button>
                          </div>
                        </div>

                        {deliveryRecord && (
                          <div className={`delivery-status ${deliveryRecord.leave ? 'leave' : ''}`}>
                            Status: {deliveryRecord.leave ? 'Leave' : deliveryRecord.delivered ? 'Delivered' : 'Pending'}
                            {!deliveryRecord.leave && deliveryRecord.delivered_quantity !== customer.default_milk_quantity && (
                              <span> (Actual: {deliveryRecord.delivered_quantity}L)</span>
                            )}
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              )}
            </div>

            <div className="daily-summary">
              <h3>Today's Delivery Summary</h3>
              <p>Total Customers for Selected Shift: {getCustomersByShift(selectedShift).length}</p>
              <p>Delivered Today: {getDeliveriesForDate().filter(d => d.delivered).length}</p>
              <p>Pending: {getDeliveriesForDate().filter(d => !d.delivered).length}</p>
            </div>
          </div>
        )}

        {activeTab === 'bills' && (
          <div className="bills-section">
            <h2>Bill Management</h2>

            {bills.length === 0 ? (
              <p>No bills generated yet.</p>
            ) : (
              <table className="bills-table">
                <thead>
                  <tr>
                    <th>Customer</th>
                    <th>Month/Year</th>
                    <th>Quantity (L)</th>
                    <th>Total Amount (₹)</th>
                    <th>Balance (₹)</th>
                    <th>Date Generated</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {bills.map(bill => (
                    <tr key={bill.id}>
                      <td>{bill.customer_name}</td>
                      <td>{bill.bill_month}/{bill.bill_year}</td>
                      <td>{bill.total_quantity.toFixed(2)}</td>
                      <td>{bill.total_amount.toFixed(2)}</td>
                      <td className={bill.balance > 0 ? 'balance-owing' : 'balance-paid'}>
                        {bill.balance.toFixed(2)}
                      </td>
                      <td>{new Date(bill.bill_generated_at).toLocaleDateString()}</td>
                      <td>
                        <span className={`status-badge ${bill.paid ? 'paid' : 'unpaid'}`}>
                          {bill.paid ? 'Paid' : 'Unpaid'}
                        </span>
                      </td>
                      <td>
                        {!bill.paid && (
                          <div className="payment-controls">
                            <input
                              type="number"
                              placeholder="Amount Paid"
                              min="0"
                              step="0.01"
                              className="payment-input"
                              onKeyDown={(e) => {
                                if (e.key === 'Enter') {
                                  const amount = e.target.value;
                                  const changeInput = e.target.nextElementSibling;
                                  const change = changeInput ? changeInput.value : '0';
                                  if (amount) {
                                    handlePartialPayment(bill.id, amount, change);
                                  }
                                }
                              }}
                            />
                            <input
                              type="number"
                              placeholder="Change Given"
                              min="0"
                              step="0.01"
                              className="change-input"
                            />
                            <button
                              onClick={(e) => {
                                const amountInput = e.target.previousElementSibling.previousElementSibling;
                                const changeInput = e.target.previousElementSibling;
                                const amount = amountInput.value;
                                const change = changeInput.value || '0';

                                if (amount) {
                                  handlePartialPayment(bill.id, amount, change);
                                  amountInput.value = '';
                                  changeInput.value = '';
                                }
                              }}
                              className="pay-btn"
                            >
                              Pay
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
